mapper.select:
select start_time, sum(${field}) as result
from tbprb
where enodeb_name = #{enodebName}
  and str_to_date(start_time, '%m/%d/%Y %H:%i:%S') between #{begin} and #{end}
group by start_time;

select a.hourtime as start_time, sum(a.${field}) as result
from (select date_format(str_to_date(start_time, '%m/%d/%Y %H:%i:%S'), '%Y-%m-%d %H:00:00') as hourtime,
             ${field}
      from tbprb
      where enodeb_name = #{enodebName}
        and str_to_date(start_time, '%m/%d/%Y %H:%i:%S') between #{begin} and #{end}
     ) a
group by a.hourtime;
