mapper.select:
select start_time, sum(${field}) as result
from tbprb
where enodeb_name = #{enodebName}
  and start_time between #{begin} and #{end}
group by start_time;

select a.hourtime as start_time, sum(a.${field}) as result
from (select date_trunc('hour', start_time) as hourtime,
             ${field}
      from tbprb
      where enodeb_name = #{enodebName}
        and start_time between #{begin} and #{end}
     ) a
group by a.hourtime;
